window.saveBlob = (function () {
  let a = document.createElement('a');
  document.body.appendChild(a);
  a.style = 'display: none';

  return function (blob, fileName) {
    let url = window.URL.createObjectURL(blob);
    a.href = url;
    a.download = fileName;
    a.click();
    window.URL.revokeObjectURL(url);
  };
})();

